mapper.delete:
delete
from testbatch
where USERID >= #{beginId,jdbcType=NUMERIC}
  and USERID <=
      #{endId,jdbcType=NUMERIC}

mapper.insert:
begin INSERT INTO testbatch (userid, username, age, phone,currencyone,countone,currencytwo,counttwo,createtime,updatetime)
SELECT #{item.userId,jdbcType=NUMERIC}, #{item.userName,jdbcType=VARCHAR}, #{item.age,jdbcType=NUMERIC}, #{item.phone,jdbcType=NUMERIC}, #{item.currencyOne,jdbcType=VARCHAR}, #{item.countOne,jdbcType=NUMERIC}, #{item.currencyTwo,jdbcType=VARCHAR}, #{item.countTwo,jdbcType=NUMERIC}, to_date(#{item.createTime,jdbcType=VARCHAR},'YYYY-MM-DD HH24:MI:SS'), sysdate FROM dual
;end;
